import { useState } from "react";
import Contacts from "./components/Contacts"

const App = () =>
{

	const [contacts, setContacts] = useState(
		{
			name: "",
			phone: "",
		}
	)

	useState(props.contacts)

	/* const [newContact, setNewContact] = useState('')
	const [newNumber, setNewNumber] = useState('') */

/* 	const newContactObj = {
		id: contacts.length + 1,
		name: newContact,
		phone: newNumber,
	} */

	const addContact = (e) => {
		e.preventDefault();
/* 		if (contacts.findIndex((person) => person.name === newContact) !== -1) 
		{ 
			alert(`${newContact} is already in the phonebook `);
			return;
		} */	

		setContacts(contacts)
		//setNewContact('')
	};

 	const handleContactChange = (e) =>
	{
		//console.log(e.target.value);
		setContacts({ name: e.target.value });
		//setNewContact(e.target.value);
	};

	const handleNumberChange = (e) =>
	{
		//console.log(e.target.value);
		//setNewNumber(e.target.value);
		setContacts({ phone: e.target.value });
	};

  return (

    <div>

		<h2>Phonebook</h2>

			<form onSubmit = { addContact }>
				<input placeholder="name" type = "text" value = { contacts.name }
					onChange = { handleContactChange }
				></input>
				<br />
				<input placeholder="phone number" type = "number" value = { contacts.phone }
						onChange = { handleNumberChange }
				></input>
				<br />
				<button type="submit">add a contact</button>
			</form>

		<h2>Numbers</h2>
			<ul style={ {listStyle: "none"} }>
        		{contacts.map(contact =>
					<Contacts key={contact.name} contact={contact} />
				)}
      		</ul>
    </div>

  );
}

export default App;
