import { useState } from "react";
import Contacts from "./components/Contacts"

const App = (props) =>
{

	const [contacts, setContacts] = useState(props.contacts)

	const [newContact, setNewContact] = useState('')

	const addContact = (e) => {
		e.preventDefault();
		//console.log("button clicked", e.target);
		if (contacts.findIndex((id) => id.name === newContact) !== -1) 
		{ 
			alert(`${newContact} is already in the phonebook `);
			return;
		}	
		const newContactObj = {
			id: contacts.length + 1,
			name: newContact,
			phone: Math.random(),
		}
		setContacts(contacts.concat(newContactObj))
		setNewContact('')
	};

	const handleContactChange = (e) =>
	{
		//console.log(e.target.value);

		setNewContact(e.target.value);
	};

  return (

    <div>

		<h2>Phonebook</h2>

			<form onSubmit = { addContact }>
				<input placeholder="name" value = { newContact }
					onChange = { handleContactChange }
				></input>
				<button type="submit">add a contact</button>
			</form>

		<h2>Numbers</h2>
			<ul style={ {listStyle: "none"} }>
        		{contacts.map(contact =>
					<Contacts key={contact.name} contact={contact} />
				)}
      		</ul>
    </div>

  );
}

export default App;
